import React, { useState, useEffect } from 'react';
import { signInWithPopup, GoogleAuthProvider, onAuthStateChanged } from "firebase/auth";
import { collection, addDoc, getDocs, updateDoc, doc } from "firebase/firestore";
import { auth, db } from './firebaseconfig'; // Ensure this path is correct
import './app.css';

function App() {
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [taskTitle, setTaskTitle] = useState('');
  const [taskDescription, setTaskDescription] = useState('');
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setUser(user);
        fetchTasks();
      } else {
        setUser(null);
      }
    });
    return () => unsubscribe();
  }, []);

  const fetchTasks = async () => {
    if (user) {
      const querySnapshot = await getDocs(collection(db, "tasks"));
      const tasksArray = querySnapshot.docs
        .filter(doc => doc.data().userId === user.uid) // Ensure tasks belong to the current user
        .map(doc => ({ id: doc.id, ...doc.data() }));
      setTasks(tasksArray);
    }
  };

  const addTask = async () => {
    if (taskTitle && taskDescription) {
      await addDoc(collection(db, "tasks"), {
        title: taskTitle,
        description: taskDescription,
        completed: false,
        userId: user.uid // Associate task with the current user
      });
      setTaskTitle('');
      setTaskDescription('');
      fetchTasks();
    }
  };

  const markAsCompleted = async (id) => {
    const taskDocRef = doc(db, "tasks", id);
    await updateDoc(taskDocRef, { completed: true });
    fetchTasks();
  };

  const markAsPending = async (id) => {
    const taskDocRef = doc(db, "tasks", id);
    await updateDoc(taskDocRef, { completed: false });
    fetchTasks();
  };

  const signIn = async () => {
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error("Error signing in:", error);
    }
  };

  const filteredTasks = tasks.filter(task => {
    if (filter === 'completed') return task.completed;
    if (filter === 'pending') return !task.completed;
    return true;
  });

  return (
    <div className="App">
      <header className="App-header">
        <h1>Task Manager</h1>
        {user ? (
          <div>
            <p>Welcome, {user.displayName || 'User'}</p>
            <div className="task-form">
              <input
                type="text"
                placeholder="Task Title"
                value={taskTitle}
                onChange={(e) => setTaskTitle(e.target.value)}
              />
              <textarea
                placeholder="Task Description"
                value={taskDescription}
                onChange={(e) => setTaskDescription(e.target.value)}
              />
              <button onClick={addTask}>Add Task</button>
            </div>
            <div className="filter-controls">
              <button onClick={() => setFilter('all')}>All Tasks</button>
              <button onClick={() => setFilter('completed')}>Completed Tasks</button>
              <button onClick={() => setFilter('pending')}>Pending Tasks</button>
            </div>
            <div className="task-list">
              {filteredTasks.map(task => (
                <div key={task.id} className={`task-item ${task.completed ? 'completed' : ''}`}>
                  <h3>{task.title}</h3>
                  <p>{task.description}</p>
                  {task.completed ? (
                    <button onClick={() => markAsPending(task.id)}>Mark as Pending</button>
                  ) : (
                    <button onClick={() => markAsCompleted(task.id)}>Mark as Completed</button>
                  )}
                </div>
              ))}
            </div>
          </div>
        ) : (
          <div>
            <p>Please log in to manage your tasks.</p>
            <button onClick={signIn}>Sign In with Google</button>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
