import React, { useState, useEffect, useCallback } from 'react';
import { signInWithPopup, GoogleAuthProvider, onAuthStateChanged } from "firebase/auth";
import { collection, addDoc, getDocs } from "firebase/firestore";
import { auth, db } from './firebaseconfig';
import './app.css';

function App() {
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [taskTitle, setTaskTitle] = useState('');
  const [taskDescription, setTaskDescription] = useState('');

  // Define fetchTasks as a callback to include in the dependency array
  const fetchTasks = useCallback(async () => {
    if (user) {
      const querySnapshot = await getDocs(collection(db, "tasks"));
      const tasksArray = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setTasks(tasksArray);
    }
  }, [user]);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setUser(user);
        fetchTasks(); // Fetch tasks whenever user is authenticated
      } else {
        setUser(null);
        setTasks([]); // Clear tasks if user logs out
      }
    });
    return () => unsubscribe();
  }, [fetchTasks]); // Include fetchTasks in dependency array

  const addTask = async () => {
    if (taskTitle && taskDescription) {
      await addDoc(collection(db, "tasks"), {
        title: taskTitle,
        description: taskDescription,
        completed: false
      });
      setTaskTitle('');
      setTaskDescription('');
      fetchTasks(); // Refresh tasks list after adding a new task
    }
  };

  const signIn = async () => {
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error("Error signing in:", error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Task Manager</h1>
        {user ? (
          <div>
            <p>Welcome, {user.displayName || 'User'}</p>
            <div className="task-form">
              <input
                type="text"
                placeholder="Task Title"
                value={taskTitle}
                onChange={(e) => setTaskTitle(e.target.value)}
              />
              <textarea
                placeholder="Task Description"
                value={taskDescription}
                onChange={(e) => setTaskDescription(e.target.value)}
              />
              <button onClick={addTask}>Add Task</button>
            </div>
            <div className="task-list">
              {tasks.map(task => (
                <div key={task.id} className={`task-item ${task.completed ? 'completed' : ''}`}>
                  <h3>{task.title}</h3>
                  <p>{task.description}</p>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <div>
            <p>Please log in to manage your tasks.</p>
            <button onClick={signIn}>Sign In with Google</button>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
